import { useState, useReducer, useContext } from 'react';
import Clothing from '../Clothing/Clothing';
import { DataContext } from '../../App';

const Cart = ({items}) => {
    const cartData=useContext(DataContext);
    console.log({cartData});

    function total(){
        let totalPrice;
        for(let i=0; i < cartData.cartItems.length; i++){
            cartData.cartItems[i]+=totalPrice;
            totalPrice;
        }
    }


    return (
        <div>
            <h2>Cart</h2>
            <div>
                {cartData.cartItems.length ===0 && <div>Cart is empty </div>}
            </div>
            {cartData.cartItems.map((item, id)=>
            <div className="cart"key={id}>
                <div>{item.title}</div>
                {/* <button onClick={()=>onAdd(item)} classname="add"> + </button> */}
                {/* <button onClick={()=>onRemove(item)} classname="remove">+</button> */}
                <div className="cart-items">
                    {/* {cartData.cartItems === item} */}
                        <div><button onClick={()=>cartData.removeProductToCart(id)}>Remove</button></div>
                    $ {item.price}
                </div>
                <div>
                    {/* {total = [cartData.cartItems].reduce((a, b) => a + b, 0)} */}
                    {cartData.cartItems.length >= 0 && <div> total: $ {total(cartData.cartItems)}</div>}
                </div>
            </div>)}
        </div>

        // {item.price += item.price-1}
        // <div className>
        //     <div className='cart-header'>
        //         <h4>Your Cart</h4>
        //         {/* <button className='open'>open</button>
        //         <button className='close'>Close</button> */}
        //     </div>
        //     {
        //         cartItems.map((item, i) => (
        //             <Clothing/>
        //         ))
        //     }
        // </div>
    );
};

export default Cart;
